/**
 * Binds a function and copies metadata.
 *
 * @private
 * @export
 * @param {Function} fn
 * @param {*} context
 * @returns {Function}
 */
export function bind(fn: Function, context: any): Function {
  return copyMetadata(fn.bind(context), fn);
